mapper.update:
update app_info
	set status = #{status}
		where id = #{id}

update app_info 
	set logoPicPath=null,logoLocPath = null 
		where id = #{id}

update app_info 
	set versionId=#{versionId}
		where id = #{id}

mapper.select:
select count(1)
	from app_info


				softwareName like CONCAT('%',#{querySoftwareName},'%')


				and status = #{queryStatus}


				and flatFormId = #{queryFlatformId}


				and categoryLevel1 = #{queryCategoryLevel1}


				and categoryLevel2 = #{queryCategoryLevel2}


				and categoryLevel3 = #{queryCategoryLevel3}

select i.*,
	(select d.valueName from data_dictionary d where d.valueId = i.flatformId and d.typeCode = 'APP_FLATFORM') as flatformName,
	(select d.valueName from data_dictionary d where d.valueId = i.status and d.typeCode = 'APP_STATUS') as statusName,
	(select v.versionNo from app_version v where v.id = i.versionId) as versionNo,
	(select c.categoryName from app_category c where c.id = i.categoryLevel1) as categoryLevel1Name,
	(select c.categoryName from app_category c where c.id = i.categoryLevel2) as categoryLevel2Name,
	(select c.categoryName from app_category c where c.id = i.categoryLevel3) as categoryLevel3Name
	from app_info i


				i.softwareName like '%${querySoftwareName}%'


				and i.status = #{queryStatus}


				and i.flatFormId = #{queryFlatformId}


				and i.categoryLevel1 = #{queryCategoryLevel1}


				and i.categoryLevel2 = #{queryCategoryLevel2}


				and i.categoryLevel3 = #{queryCategoryLevel3}


			order by i.creationDate DESC
			limit #{startIndex},#{pageSize}
